import { createSlice } from "@reduxjs/toolkit";

const friendSlice = createSlice({
  name: "friends",
  initialState: {
    friends: [],
    requests: [],
    loading: false,
    error: null,
  },
  reducers: {
    fetchFriendsRequest: (state) => {
      state.loading = true;
      state.error = null;
    },
    fetchFriendsSuccess: (state, action) => {
      state.loading = false;
      state.friends = action.payload;
    },
    fetchFriendsFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

    fetchFriendRequestsRequest: (state) => {
      state.loading = true;
      state.error = null;
    },
    fetchFriendRequestsSuccess: (state, action) => {
      state.loading = false;
      state.requests = action.payload;
    },
    fetchFriendRequestsFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

    removeFriendRequest: (state) => {
      state.loading = true;
      state.error = null;
    },
    removeFriendSuccess: (state, action) => {
      state.loading = false;
      state.friends = state.friends.filter((friend) => friend.id !== action.payload);
    },
    removeFriendFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

    acceptFriendRequestRequest: (state) => {
      state.loading = true;
      state.error = null;
    },
    acceptFriendRequestSuccess: (state, action) => {
      state.loading = false;
      state.friends.push(action.payload);
      state.requests = state.requests.filter((req) => req.id !== action.payload.id);
    },
    acceptFriendRequestFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
  },
});

export const {
  fetchFriendsRequest,
  fetchFriendsSuccess,
  fetchFriendsFailure,
  fetchFriendRequestsRequest,
  fetchFriendRequestsSuccess,
  fetchFriendRequestsFailure,
  removeFriendRequest,
  removeFriendSuccess,
  removeFriendFailure,
  acceptFriendRequestRequest,
  acceptFriendRequestSuccess,
  acceptFriendRequestFailure,
} = friendSlice.actions;

export default friendSlice.reducer;
