import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchFriendsRequest,
  fetchFriendRequestsRequest,
  acceptFriendRequestRequest,
  removeFriendRequest
} from "../store/friendSlice";
import "../styles/friends.css"; 

const Friends = () => {
  const dispatch = useDispatch();
  const {
    friendList: { friends = [], page: friendPage, total: friendTotal },
    requestList: { requests = [], page: requestPage, total: requestTotal },
    loading,
    error
  } = useSelector((state) => state.friends);
  const { user } = useSelector((state) => state.auth);

  useEffect(() => {
    if (user) {
      dispatch(fetchFriendsRequest({ userId: user._id, page: 1 }));
      dispatch(fetchFriendRequestsRequest({ userId: user._id, page: 1 }));
    }
  }, [dispatch, user]);

  const handleLoadMoreFriends = () => {
    if (friendPage < friendTotal) {
      dispatch(fetchFriendsRequest({ userId: user._id, page: friendPage + 1 }));
    }
  };

  const handleLoadMoreRequests = () => {
    if (requestPage < requestTotal) {
      dispatch(fetchFriendRequestsRequest({ userId: user._id, page: requestPage + 1 }));
    }
  };

  const handleRemoveFriend = (friend) => {
    dispatch(removeFriendRequest({ userId: user._id, friendId: friend._id }));
  };

  const handleAcceptFriend = (friend) => {
    dispatch(acceptFriendRequestRequest({ userId: user._id, friendId: friend._id }));
  };

  return (
    <div className="friends-container">
      <h2>Your Friends</h2>
      {loading ? <p>Loading...</p> : (
        <div className="friends-list">
          {friends.length > 0 ? (
            friends.map((friend) => (
              <div key={friend._id} className="friend-card">
                <img src={friend.profilePicture || "/default_avatar.png"} alt="Profile" className="profile-pic" />
                <div className="friend-info">
                  <p className="friend-name">{friend.name}</p>
                  <button className="remove-btn" onClick={() => handleRemoveFriend(friend)}>Remove</button>
                </div>
              </div>
            ))
          ) : <p>No friends yet.</p>}
        </div>
      )}

      {friendPage < friendTotal && (
        <button className="load-more-btn" onClick={handleLoadMoreFriends}>
          Load More Friends
        </button>
      )}

      <h2>Friend Requests</h2>
      {requests.length > 0 ? (
        <div className="requests-list">
          {requests.map((request) => (
            <div key={request._id} className="friend-card">
              <img src={request.profilePicture || "/default_avatar.png"} alt="Profile" className="profile-pic" />
              <div className="friend-info">
                <p className="friend-name">{request.name}</p>
                <button className="accept-btn" onClick={() => handleAcceptFriend(request)}>Accept</button>
              </div>
            </div>
          ))}
        </div>
      ) : <p>No pending requests.</p>}

      {requestPage < requestTotal && (
        <button className="load-more-btn" onClick={handleLoadMoreRequests}>
          Load More Requests
        </button>
      )}
    </div>
  );
};

export default Friends;
