import { createSlice } from "@reduxjs/toolkit";

const friendSlice = createSlice({
  name: "friends",
  initialState: {
    friends: [],
    requests: [],
    loading: false,
    error: null,
  },
  reducers: {
    fetchFriendsRequest: (state) => { state.loading = true; },
    fetchFriendsSuccess: (state, action) => {
      state.loading = false;
      state.friends = action.payload;
    },
    fetchFriendsFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
    addFriendRequest: (state, action) => { state.requests.push(action.payload); },
    acceptFriendRequest: (state, action) => { state.friends.push(action.payload); },
  },
});

export const { fetchFriendsRequest, fetchFriendsSuccess, fetchFriendsFailure, addFriendRequest, acceptFriendRequest } = friendSlice.actions;
export default friendSlice.reducer;
