import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchFriendsRequest, acceptFriendRequestRequest, removeFriendRequest } from "../store/friendSlice";
import { socket, setupSocketListeners } from "../utils/socket";
import "../styles/friends.css"; // Import styles

const Friends = () => {
  const dispatch = useDispatch();
  const { friends, requests, loading, error } = useSelector((state) => state.friends);
  const { user } = useSelector((state) => state.auth);

  useEffect(() => {
    if (user) {
      dispatch(fetchFriendsRequest(user._id));
      //dispatch(fetchFriendRequestsRequest());



      // Setup WebSocket listeners
      setupSocketListeners(dispatch, user._id);
    }

    return () => {
      socket.off("friend_request");
      socket.off("friend_added");
      socket.off("friend_removed");
    };
  }, [dispatch, user]);

  const handleRemoveFriend = (friend) => {
    dispatch(removeFriendRequest(friend._id));

    socket.emit("remove_friend", { userId: user._id, friendId: friend._id });
  };

  const handleAcceptFriend = (friend) => {
    dispatch(acceptFriendRequest(friend._id));

    socket.emit("accept_friend", { userId: user._id, friendId: friend._id });
  }

  return (
    <div className="friends-container">
      <h2>Your Friends</h2>

      {loading ? <p>Loading...</p> : (
        <div className="friends-list">
          {friends.length > 0 ? (
            friends.map((friend) => (
              <div key={friend._id} className="friend-card">
                <img src={friend.profilePicture || "/default-avatar.png"} alt="Profile" className="profile-pic" />
                <div className="friend-info">
                  <p className="friend-name">{friend.name}</p>
                  <button className="remove-btn" onClick={() => handleRemoveFriend(friend)}>Remove</button>
                </div>
              </div>
            ))
          ) : <p>No friends yet.</p>}
        </div>
      )}

      <h2>Friend Requests</h2>
      {requests.length > 0 ? (
        <div className="requests-list">
          {requests.map((request) => (
            <div key={request.id} className="friend-card">
              <img src={request.profilePicture || "/default-avatar.png"} alt="Profile" className="profile-pic" />
              <div className="friend-info">
                <p className="friend-name">{request.name}</p>
                <button className="accept-btn" onClick={() => handleAcceptFriend(request)}>Accept</button>
              </div>
            </div>
          ))}
        </div>
      ) : <p>No pending requests.</p>}
    </div>
  );
};

export default Friends;
