import React, {useEffect} from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Provider, useDispatch, useSelector } from "react-redux";
import store from "./store";
import Login from "./pages/login";
import Logout from './components/logout';
import Friends from "./pages/friends";
import {loginSuccess} from "./store/authSlice";
import AddFriend from "./components/addFriend";
import Notification from "./components/notification";
import ProtectedRoute from "./utils/protectedRoute";
import Register from "./pages/register";

const App = () => {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    // Check if the user was logged in
    const savedToken = localStorage.getItem("token") || sessionStorage.getItem("token");
    const savedUser = localStorage.getItem("user") || sessionStorage.getItem("user");

    if (savedToken && savedUser) {
      dispatch(loginSuccess({ token: savedToken, user: JSON.parse(savedUser), rememberMe: true }));
    }
  }, [dispatch]);

  return (
    <Provider store={store}>
      <Router>
        <nav>
          <a href="/login">Login</a> | <a href="/register">Register</a> | <a href="/friends">Friends</a>
          {/* <a href="/login">Login</a> | <a href="/friends">Friends</a> | <a href="/add-friend">Add Friend</a> */}
          <Logout />
        </nav>
        <Routes>
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route
            path="/friends"
            element={
              <ProtectedRoute>
                <Friends />
              </ProtectedRoute>
            }
          />
          <Route
            path="/add-friend"
            element={
              <ProtectedRoute>
                <AddFriend />
              </ProtectedRoute>
            }
          />
        </Routes>
      </Router>
    </Provider>
  );
};

export default App;
