import { call, put, takeLatest } from "redux-saga/effects";
import axios from "axios";
import { fetchFriendsRequest, fetchFriendsSuccess, fetchFriendsFailure } from "../store/friendSlice";

function* fetchFriendsSaga() {
  try {
    const token = localStorage.getItem("token");
    const response = yield call(axios.get, "http://localhost:5000/api/friends/friends", {
      headers: { Authorization: `Bearer ${token}` },
    });
    yield put(fetchFriendsSuccess(response.data.friends));
  } catch (error) {
    yield put(fetchFriendsFailure(error.response.data.message));
  }
}

export default function* watchFriendSaga() {
  yield takeLatest(fetchFriendsRequest.type, fetchFriendsSaga);
}
