import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { Provider, useDispatch, useSelector } from "react-redux";
import store from "./store";
import { loginSuccess } from "./store/authSlice";
import ProtectedRoute from "./utils/protectedRoute";
import LoginRegister from "./pages/login";
import Friends from "./pages/friends";
import Logout from "./components/logout";
import AddFriend from "./components/addFriend";
import Notification from "./components/notification";
import Sidebar from "./components/sidebar";
import "./styles/app.css";
//import "./styles/global.css"; // Import a global stylesheet for consistency

const App = () => {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    // Check if the user was already logged in
    const savedToken = localStorage.getItem("token") || sessionStorage.getItem("token");
    const savedUser = localStorage.getItem("user") || sessionStorage.getItem("user");

    const user = savedUser ? JSON.parse(savedUser) : null;

    if (savedToken && savedUser) {
      dispatch(loginSuccess({ token: savedToken, user, rememberMe: true }));
    }
  }, [dispatch]);

  return (
    <Provider store={store}>
      <Router>
        {token && <Sidebar />}
        {/* {token && <Logout />} Show Logout button only if logged in */}
        <Routes>
          {/* If user is logged in, redirect to Friends. Otherwise, show Login/Register */}
          <Route path="/" element={token ? <Navigate to="/friends" /> : <LoginRegister />} />

          <Route
            path="/friends"
            element={
              <ProtectedRoute>
                <Friends />
              </ProtectedRoute>
            }
          />
          <Route
            path="/add-friend"
            element={
              <ProtectedRoute>
                <AddFriend />
              </ProtectedRoute>
            }
          />
        </Routes>
      </Router>
    </Provider>
  );
};

export default App;
