import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchFriends, removeFriend } from "../store/friendSlice"; // Import async thunks

const Friends = () => {
  const dispatch = useDispatch();

  // Selectors to access Redux state
  const friends = useSelector((state) => state.friends.friends); // Friends list
  const loading = useSelector((state) => state.friends.loading); // Loading state
  const error = useSelector((state) => state.friends.error); // Error state

  // Fetch friends when the component mounts
  useEffect(() => {
    dispatch(fetchFriends());
  }, [dispatch]);

  // Handle removing a friend
  const handleRemoveFriend = (friendId) => {
    dispatch(removeFriend(friendId));
  };

  if (loading) return <p>Loading friends...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="friends-container">
      <h1>Your Friends</h1>
      {friends.length === 0 ? (
        <p>You have no friends yet!</p>
      ) : (
        <ul>
          {friends.map((friend) => (
            <li key={friend.id} className="friend-item">
              <span>{friend.name}</span>
              <button
                className="remove-button"
                onClick={() => handleRemoveFriend(friend.id)}
              >
                Remove
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Friends;
