import { createSlice } from "@reduxjs/toolkit";

const friendSlice = createSlice({
  name: "friends",
  initialState: {
    friendList: {
      friends: []
    },
    requestList: {
      requests: []
    },
    loading: false,
    error: null,
  },
  reducers: {
    fetchFriendsRequest: (state) => {
      state.loading = true;
      state.error = null;
    },
    fetchFriendsSuccess: (state, action) => {
      state.loading = false;
      state.friendList = action.payload;
    },
    fetchFriendsFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

    fetchFriendRequestsRequest: (state) => {
      state.loading = true;
      state.error = null;
    },
    fetchFriendRequestsSuccess: (state, action) => {
      state.loading = false;
      state.requestList = action.payload;
    },
    fetchFriendRequestsFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

    sendFriendRequestRequest: (state, action) => {
      state.loading = true;
      state.error = null;
    },
    sendFriendRequestSuccess: (state, action) => {
      state.loading = false;
      state.requestList.requests = [...state.requestList.requests, action.payload]
    },
    sendFriendRequestFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

    removeFriendRequest: (state) => {
      state.loading = true;
      state.error = null;
    },
    removeFriendSuccess: (state, action) => {
      state.loading = false;
      state.friendList = state.friendList.friends.filter((friend) => friend.id !== action.payload);
    },
    removeFriendFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

    acceptFriendRequestRequest: (state) => {
      state.loading = true;
      state.error = null;
    },
    acceptFriendRequestSuccess: (state, action) => {
      state.loading = false;
      if (state.friendList) {
        state.friendList.friends.push(action.payload);
      } else {
        state.friendList = {friends: [action.payload]};
      }

      state.requestList = state.requestList.requests.filter((req) => req.id !== action.payload.id);
    },
    acceptFriendRequestFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
  },
});

export const {
  fetchFriendsRequest,
  fetchFriendsSuccess,
  fetchFriendsFailure,
  fetchFriendRequestsRequest,
  fetchFriendRequestsSuccess,
  fetchFriendRequestsFailure,
  sendFriendRequestRequest,
  sendFriendRequestSuccess,
  sendFriendRequestFailure,
  removeFriendRequest,
  removeFriendSuccess,
  removeFriendFailure,
  acceptFriendRequestRequest,
  acceptFriendRequestSuccess,
  acceptFriendRequestFailure,
} = friendSlice.actions;

export default friendSlice.reducer;
