import { call, put, takeLatest } from "redux-saga/effects";
import axios from "axios";
import {
  fetchFriendsRequest,
  fetchFriendsSuccess,
  fetchFriendsFailure,
  removeFriendRequest,
  removeFriendSuccess,
  removeFriendFailure,
} from "../store/friendSlice";

// Fetch Friends Saga
function* fetchFriendsSaga(action) {
  try {
    const token = localStorage.getItem("token");
    const { search, page } = action.payload;
    const response = yield call(
      axios.get,
      `http://localhost:5000/api/friends?search=${search}&page=${page}`,
      { headers: { Authorization: `Bearer ${token}` } }
    );

    yield put(fetchFriendsSuccess(response.data));
  } catch (error) {
    yield put(fetchFriendsFailure(error.response?.data?.message || "Error fetching friends"));
  }
}


// Remove Friend Saga
function* removeFriendSaga(action) {
  try {
    const token = localStorage.getItem("token");
    yield call(axios.delete, `http://localhost:5000/api/friends/remove/${action.payload}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    yield put(removeFriendSuccess(action.payload)); // Dispatch success action with friend ID
  } catch (error) {
    yield put(removeFriendFailure(error.response?.data?.message || "Failed to remove friend")); // Handle error
  }
}

// Watcher Saga
export default function* watchFriendSaga() {
  yield takeLatest(fetchFriendsRequest.type, fetchFriendsSaga); // Watch for fetchFriendsRequest
  yield takeLatest(removeFriendRequest.type, removeFriendSaga); // Watch for removeFriendRequest
}
